-- Librarieslocal lurker = require 'Utilities.lurker.lurker'Input = require 'Utilities.thomas.Input'Car = require 'Source.Car'Car:setState( 'Running' )function love.load()	input = Input()	input:bind( 'escape', love.event.quit )		Cars = {		Honda = Car( 'Honda' ), 		Chevy = Car( 'Chevy' ), 		Ford = Car( 'Ford' ), 	}	local function printCar()		for _, v in pairs( Cars ) do			v:speak()		end	end	-- printCar()	-- Cars.Ford:setState( 'Running' )	-- printCar()	-- Cars.Ford:popState()	-- printCar()	-- Cars.Honda:setState( 'Broken' )	-- Cars.Chevy:setState( 'Running' ))	Cars.Honda:setState( 'Broken' )	printCar()endfunction love.update( dt )			lurker.update()	input:update( dt ) endfunction love.draw()endfunction love.keypressed( key, isRepeat )	input:keypressed( key )endfunction love.keyreleased( key, isRepeat )	input:keyreleased( key )endfunction love.mousepressed( x, y, button )	input:mousepressed( button )endfunction love.mousereleased( x, y, button )	input:mousereleased( button )endfunction love.gamepadpressed( joystick, button )	input:gamepadpressed( joystick, button )endfunction love.gamepadreleased( joystick, button )	input:gamepadreleased( joystick, button )endfunction love.gamepadaxis( joystick, axis, value )	input:gamepadaxis( joystick, axis, value )endfunction love.quit()end