local Camera = require 'Utilities.Camera'local Base = require 'Source.Entities.Base'local OldNew = Base.Newfunction love.load()	Cam = Camera.New{ 		x = 400, 		y = 300, 		Width = 800, 		Height = 600, 	}		local Car = Base:Extend()	Car.New = function( x, y ) return OldNew( x, y ) end	a = Car.New( 32, 32 )	a.Width = 32	a.Height = 32		Cam:InsideView( a )endfunction love.draw()endfunction love.update( dt )endfunction love.mousepressed( x, y, Button )endfunction love.keypressed( Key, IsRepeat )	if Key == 'escape' then love.event.quit() endend